
public class ArrayEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] numArr = new int[3];
		numArr[0] = 1;
		numArr[1] = 2;
		numArr[2] = 3;
		
		for (int i = 0; i < numArr.length; i++) {
			System.out.println((i+1) + " 번째 수행: " + numArr[i]);
			
		}
		
		
		
	}

}
