
public class CastingExTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 0;
		double doubleNum = 21.318d;
		n = (int)doubleNum;
		
		System.out.println("n : " + n);
		System.out.println();
		
		doubleNum = (double)n;
		
		System.out.println("doubleNum : " + doubleNum);
		System.out.println();
		
		
		
	}

}
