
public class FlowTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

/*		성적 60점 이상 은합격
  		그 이외에는 불합격		
		
		60점은 합격입니다
*/		
		
		int score = 59;
		
		if (score >= 60) {
			System.out.println(score + " 점은 합격입니다");
		}else{
			System.out.println(score + " 점은 불합격입니다");
		}
		

		
		
		
		
	}

}
