
public class ForFlowTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int mul = 0;
		int cor = 0;
		
		mul = 2;
				
		
		for (int i = 1; i < 10; i++) {
			cor = mul * i;
			
			System.out.println("2 * " + i + " = " + cor);
		}
		
				
		
		for (int i = 1; i <= 9; i++) {
			System.out.print("2 * " + i + " = " + (2 * i) + "\t");
			
		}
		
		
		
	}

}
