
public class OperEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

/*		단항 연산자 (--) : 피연산자의 값을 1 감소시킨다. 
 * 		증감 연산자	(++) : 피연산자의 값을 1 증가시킨다. 
 */
		
		int i = 5;
	//	i++; 후위형
		i--;
		
		
		System.out.println(i);
		
		i = 5;
	//	++i; 전위형 
		
		System.out.println(i);
		
	/*  n = 5; 
		
		n++;
		n;
		n = n + 1;
		
		++n;
		n = n + 1;
		
	*/
		
		i = 5;
		
		System.out.println(i++);
		System.out.println(i);
//		
		i = 5;
		
		System.out.println(++i);
		System.out.println(i);
		
		
		
		
		
		
	}

}
