
public class OperEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 부정연산자 ! -> true를 false로 바꾼다. false는 true로.
		
		
		boolean power = false;
		
		
		System.out.println("power : " + power);
		System.out.println();
		
		power = !power;
		
		System.out.println("power : " + power);
		System.out.println();
		
		power = !power;
		System.out.println("power : " + power);
		System.out.println();
		
		
		
		
		
		
	}

}
